raoblig=true;\r\n			document.getElementById(\"labelotracantidad\").className = \"obligatorio\";\r\n		} else {\r\n			otraoblig=false;\r\n			document.getElementById(\"labelotracantidad\").className = \"\";\r\n			eval(\"document.F.otracantidadnok.style.display=\'none\';\");\r\n			eval(\"document.F.otracantidadok.style.display=\'none\';\");\r\n		}\r\n	}\r\n	if (vname==\"domiciliacion\") {\r\n		if(value==\"domiciliacion\") {\r\n			tarjeta=false;\r\n			document.getElementById(\"labeltitular\").className=\'obligatorio\';\r\n			document.getElementById(\"labelentidad\").className=\'obligatorio\';\r\n			document.getElementById(\"labeloficina\").className=\'obligatorio\';\r\n			document.getElementById(\"labeldc\").className=\'obligatorio\';\r\n			document.getElementById(\"labelcuenta\").className=\'obligatorio\';\r\n			eval(\"document.F.titular.disabled=false;\");\r\n			eval(\"document.F.entidad.disabled=false;\");\r\n			eval(\"document.F.oficina.disabled=false;\");\r\n			eval(\"document.F.dc.disabled=false;\");\r\n			eval(\"document.F.cuenta.disabled=false;\");\r\n			return;\r\n		} else {\r\n			tarjeta=true;\r\n			document.getElementById(\"labeltitular\").className=\'\';\r\n			document.getElementById(\"labelentidad\").className=\'\';\r\n			document.getElementById(\"labeloficina\").className=\'\';\r\n			document.getElementById(\"labeldc\").className=\'\';\r\n			document.getElementById(\"labelcuenta\").className=\'\';\r\n			eval(\"document.F.titular.disabled=true;\");\r\n			eval(\"document.F.entidad.disabled=true;\");\r\n			eval(\"document.F.oficina.disabled=true;\");\r\n			eval(\"document.F.dc.disabled=true;\");\r\n			eval(\"document.F.cuenta.disabled=true;\");\r\n			return;\r\n		}\r\n	}\r\n	if (vname==\"otracantidad\") {\r\n		if(otraoblig==false) {\r\n			eval(\"document.F.otracantidadnok.style.display=\'none\';\");\r\n			eval(\"document.F.otracantidadok.style.display=\'none\';\");\r\n			return;\r\n		}\r\n	}\r\n	if (vname==\"pais\") {\r\n		if (value==\'España\') {\r\n			provob