	alert(\'Debes cumprimentar o Nº da vía, teu Piso, Letra, etc.\');\r\n		return false;\r\n	}\r\n	if (document.F.codpostal.value.length==0) {\r\n		alert(\'Debes cumprimentar o teu Código postal\');\r\n		return false;\r\n	}\r\n	if (document.F.poblacion.value.length==0 && poboblig==true) {\r\n		alert(\'Debes cumprimentar a túa Poboación\');\r\n		return false;\r\n	}\r\n	if (document.F.provincia.value.length==0 && provoblig==true) {\r\n		alert(\'Debes cumprimentar a túa Provincia\');\r\n		return false;\r\n	}\r\n	if (document.F.dianacimiento.value.length==0) {\r\n		alert(\'Debes cumprimentar o teu Día de nacemento\');\r\n		return false;\r\n	}\r\n	if (document.F.mesnacimiento.value.length==0) {\r\n		alert(\'Debes cumprimentar o teu Mes de nacemento\');\r\n		return false;\r\n	}\r\n	if (document.F.anonacimiento.value.length==0) {\r\n		alert(\'Debes cumprimentar o teu Ano de nacemento\');\r\n		return false;\r\n	}\r\n	if (document.getElementById(\'email2\').value.length==0) {\r\n		alert(\'Debes cumprimentar unha dirección de Correo-e\');\r\n		return false;\r\n	}\r\n	if (!comprobaremail(document.getElementById(\'email2\').value)) {\r\n		alert(\'Debes cumprimentar unha dirección correcta de Correo-e\');\r\n		return false;\r\n	}\r\n	if (document.F.clave.value.length==0) {\r\n		alert(\'Debes cumprimentar un Contrasinal\');\r\n		return false;\r\n	}\r\n//	if (document.F.telefono.value.length==0) {\r\n//		alert(\'Debes cumprimentar o teu Teléfono\');\r\n//		return false;\r\n//	}\r\n	if (document.F.otracantidad.value.length==0 && otraoblig==true) {\r\n		alert(\'Debes cumprimentar a Outra cantidade\');\r\n		return false;\r\n	}\r\n	if (document.F.titular.value.length==0 && tarjeta==false) {\r\n		alert(\'Debes cumprimentar o Titular da conta\');\r\n		return false;\r\n	}\r\n	if (document.F.entidad.value.length==0 && tarjeta==false) {\r\n		alert(\'Debes cumprimentar a Entidade\');\r\n		return false;\r\n	}\r\n	if (document.F.oficina.value.length==0 && tarjeta==false) {\r\n		alert(\'Debes cum