vIBAN=false;\r\nvar cuentavSWIFT=false;\r\nfunction captchavalid(val) {\r\n	captchav=val;\r\n}\r\nfunction cuentavalidIBAN(val) {\r\n	cuentavIBAN=val;\r\n}\r\nfunction cuentavalidSWIFT(val) {\r\n	cuentavSWIFT=val;\r\n}\r\nfunction comprobaremail(email) {\r\n	if (email==undefined) return false;\r\n	if (email==\"\") return false;\r\n	var patron = /[\\w-\\.]{3,}@([\\w-]{2,}\\.)*([\\w-]{2,}\\.)[\\w-]{2,4}/;\r\n	if (email.match(patron)) return true;\r\n	else return false;\r\n}\r\nfunction poneEnlace(value) {\r\n	a = document.getElementById(\"linkcons\");\r\n	if (value==\"Gestion-Administracion\") {\r\n		document.F.recibirinfo.checked=false;\r\n		document.getElementById(\"opcproy1\").style.display=\'none\';\r\n		document.getElementById(\"opcproy2\").style.display=\'none\';\r\n		document.getElementById(\"opcproy3\").style.display=\'none\';\r\n		document.getElementById(\"opcgest\").style.display=\'block\';\r\n	} else {\r\n		document.F.recibirinfo.checked=true;\r\n		document.getElementById(\"opcgest\").style.display=\'none\';\r\n		document.getElementById(\"opcproy1\").style.display=\'block\';\r\n		document.getElementById(\"opcproy2\").style.display=\'block\';\r\n		document.getElementById(\"opcproy3\").style.display=\'block\';\r\n	}\r\n	a.setAttribute(\"href\", \"index.php?V_dir=MSC&V_mod=showart&id=81&idproyecto=\"+encodeURI(document.F.proyecto.value));\r\n}\r\nfunction checkF(vname,value) {\r\n	var result=true;\r\n	if (vname==\"email\") result=comprobaremail(value);\r\n	if (vname==\"proyecto\") poneEnlace(value);\r\n	if (vname==\"cantidad\") {\r\n		if (value==\"x\") {\r\n			otraoblig=true;\r\n			document.getElementById(\"labelotracantidad\").className = \"obligatorio\";\r\n		} else {\r\n			otraoblig=false;\r\n			document.getElementById(\"labelotracantidad\").className = \"\";\r\n			eval(\"document.F.otracantidadnok.style.display=\'none\';\");\r\n			eval(\"document.F.otracantidadok.style.display=\'none\';\");\r\n		}\r\n	}\r\n	if (vname==\"domiciliacion\") {\r\n		if(value==\"domiciliacion\") {\r\n			tarjeta=false;\r\n			document.getElementById(\"labeltitular\").className=\'obligatorio\';\r\n			document.getElementById(\"labelIBAN\").className=\'obligatorio\';\r\n			document.getElementById(\"labelSWIFT\").className=\'obligatorio\';\r\n			eval(\"document.F.titular.disabled=false;\");\r\n			eval(\"document.F.IBAN.disabled=false;\");\r\n			eval(\"document.F.SWIFT.disabled=false;\");\r\n			return;\r\n		} else {\r\n			tarjeta=true;\r\n			document.getElementById(\"labeltitular\").className=\'\';\r\n			document.getElementById(\"labelIBAN\").className=\'\';\r\n			document.getElementById(\"labelSWIFT\").className=\'\';\r\n			eval(\"document.F.titular.disabled=true;\");\r\n			eval(\"document.F.IBAN.disabled=true;\");\r\n			eval(\"document.F.SWIFT.disabled=true;\");\r\n			return;\r\n		}\r\n	}\r\n	if (vname==\"otracantidad\") {\r\n		if(otraoblig==false) {\r\n			eval(\"document.F.otracantidadnok.style.display=\'none\';\");\r\n			eval(\"document.F.otracantidadok.style.display=\'none\';\");\r\n			return;\r\n		}\r\n		if(otraoblig==true) {\r\n			if (parseFloat(value)>0) {\r\n				eval(\"document.F.otracantidadnok.style.display=\'none\';\");\r\n				eval(\"document.F.otracantidadok.style.display=\'block\';\");\r\n			} else {\r\n				eval(\"document.F.otracantidadok.style.display=\'none\';\");\r\n				eval(\"document.F.otracantidadnok.style.display=\'block\';\");\r\n				return false;\r\n			}\r\n		}\r\n	}\r\n	if (vname==\"pais\") {\r\n		if (value==\'España\') {\r\n			provoblig=true;\r\n			document.getElementById(\"labelprovincia\").className = \"obligatorio\";\r\n//			poboblig=true;\r\n//			document.getElementById(\"labelpoblacion\").className = \"obligatorio\";\r\n		} else {\r\n			provoblig=false;\r\n			document.getElementById(\"labelprovincia\").className = \"\";\r\n//			poboblig=false;\r\n//			document.getElementById(\"labelpoblacion\").className = \"\";\r\n			eval(\"document.F.provinciaok.style.display=\'none\';\");\r\n			eval(\"document.F.provincianok.style